import './App.css';
import { Component } from 'react';
import Form from './Form';
import Journal from './Journal';
import Introduction from './Introduction';

class App extends Component {

  state= {
    date: [],
    unrealisticThought: "",
    unrealisticThoughts: [],
    cognitiveDistortions: [],
    realisticReframe: "",
    realisticReframes: [],
  }

  addUnrealisticThoughts = (event) => {
    this.setState({unrealisticThought: event.target.value})
  }

  addRealisticReframes = (event) => {
    this.setState({realisticReframe: event.target.value})
  }

  // create functions for adding strings to arrays
  // push arrays to journal
  // map over arrays in journal and render the entries to the page, possibly in components 

  render(){
    return (
      <div className="App">
        <h1>Self-Talk Helper</h1>
        <Introduction />
        <Form addUnrealisticThoughts={this.addUnrealisticThoughts} addRealisticReframes={this.addRealisticReframes}/>
        <h1>Your Self-Talk Helper Journal</h1>
        <Journal />
      </div>
    );
  }
}

export default App;
